mapper.select:
 SELECT T.*
 FROM(SELECT ROW_NUMBER() OVER(ORDER BY NUM_BID DESC) AS RNUM,







						 B.TITLE LIKE '%' || #{searchText} || '%' 


						 B.CONTENT LIKE '%'|| #{searchText} ||'%' 


						 U.USERS_NAME LIKE '%'|| #{searchText} ||'%' 





		AND B.BBS_TYPE =#{bbs_type}

	) T
	WHERE T.RNUM >= #{fromRowNum} AND T.RNUM <= #{toRowNum}

mapper.update:
	UPDATE BBS SET	 
	 TITLE = #{title} 
 	,CONTENT = #{content}
 	,UPDATE_UID = #{updId}    		 	
 	,UPDATE_DTTM = SYSDATE  
	 WHERE NUM_BID = #{num_bid}

mapper.insert:
INSERT INTO BBS 
( 
NUM_BID
,BBS_TYPE
,FIRST_WORD
,TITLE 
,CONTENT 
,FID
,CREATE_UID 
,CREATE_DTTM 
) 
VALUES 
(
 BBS_SEQ.NEXTVAL 
,#{bbs_type}
,#{first_word}
,#{title} 	
,#{content} 	
,#{fid, jdbcType=NUMERIC}
,#{user_id} 	
,SYSDATE 	
)


 	SELECT ATTACHED_FILE_FID.NEXTVAL FROM DUAL

 INSERT INTO ATTACHED_FILE
		 ( 
		 FID 
		 ,FILENAME 
		 ,FILEPATH 
		 ,SUB_FID
		 ) 
		 VALUES 
		 ( 
		  #{fid} 	
		 ,#{filename} 	
		 ,#{filepath} 
		 ,#{fid}	
		 )
